export default {
  name: 'CommonForm',
  props: {
    formLabel: Array,
    inline: Boolean
  },
  computed: {
    form() {
      return this.initForm();
    }
  },
  methods: {
    initForm() {
      const form = {};
      if (this.formLabel) {
        this.formLabel.forEach(item => {
          form[item.model] = '';
        });
      }
      return form;
    }
  }
};
