import Mock from "mockjs";
export default {
   getMenu: config => {
    const { username, password } = JSON.parse(config.body)
    //先判斷用戶是否存在
    //判斷帳號和密碼是否對應
    if (username === 'admin' && password === 'admin'){
        return {
            code: 2000,
            data: {
                menu: [
                    {
                        path: '/',
                        name: 'home',
                        label: '首頁',
                        icon: 'HomeFilled',
                        url: 'home/index'
                    },
                    {
                        path: '/mall',
                        name: 'mall',
                        label: '商品管理',
                        icon: 'video-play',
                        url: 'mall/index'
                    },
                    {
                        path: '/user',
                        name: 'user',
                        label: '用戶管理',
                        icon: 'user',
                        url: 'User/index'
                    },
                    {
                        path: '/other',
                        label: '其他',
                        icon: 'location',
                        children: [
                            {
                                path: '/page1',
                                name: 'page1',
                                label: '頁面1',
                                icon: 'Setting',
                                url: 'Other/PageOne.vue'
                            },
                            {
                                path: '/page2',
                                name: 'page2',
                                label: '頁面2',
                                icon: 'setting',
                                url: 'Other/PageTwo.vue'
                            }
                        ]
                    }
                ],
                token: Mock.Random.guid(),
                message: '獲取成功'
            }
        }
    } else if (username === 'test' && password === "Test123") {
        return {
            code: 20000,
            data: {
                menu: [
                    {
                        path: '/',
                        name: 'home',
                        label: '首頁',
                        icon: 'HomeFilled',
                        url: 'home/index'
                    },
                    {
                        path: '/mall',
                        name: 'mall',
                        label: '商品管理',
                        icon: 'video-play',
                        url: 'mall/index'
                    }
                ],
                token: Mock.Random.guid(),
                message: '獲取成功'
            }
        }
    } else {
        return {
            code: -999,
            data: {
                message: '密碼錯誤'
            }
        }
    }
   }
}
